use mpint::*;

#[derive(Deserialize, Debug)]
#[allow(dead_code)]
pub struct DSSPublicKey {
    _type: String,
    p: MPUint,
    q: MPUint,
    g: MPUint,
    y: MPUint,
}

#[derive(Deserialize, Debug)]
#[allow(dead_code)]
pub struct DSSSignature {
    _type: String,
    r: [u8; 20],
    s: [u8; 20],
}

#[cfg(test)]
mod test {
    use super::*;
    use super::super::base64;
    use super::super::serde_de;

    #[test]
    fn signature_deserializes() {
        let _ : DSSSignature = serde_de::from_slice(&base64::decode("AAAAB3NzaC1kc3MAAAAoo3+JKSK924b7FHg88V2n338fAsRiCnQLHJLgZUHdvGkc9RnjfVwKOg==").unwrap()).unwrap();
    }

    #[test]
    fn signature_verifies() {
        let _pubkey : DSSPublicKey = serde_de::from_slice(&base64::decode("").unwrap()).unwrap();
        let _sig : DSSSignature = serde_de::from_slice(&base64::decode("AAAAB3NzaC1kc3MAAAAoo3+JKSK924b7FHg88V2n338fAsRiCnQLHJLgZUHdvGkc9RnjfVwKOg==").unwrap()).unwrap();
        let _data = base64::decode("AAAAIOM9VvUIDf3xKQ7XsJsoRVc75coCBdyIRzWx/pri9ivXMg==").unwrap();
    }
}
